// Compute a stable projectKey (sha1) from jar list for Redis cache namespacing.
// Input: /api/get_jars_list response (JSON array of jar paths).
const crypto = require('crypto');

let jars = [];
for (const it of $input.all()) {
  const j = it.json;
  if (Array.isArray(j)) jars.push(...j);
  else if (Array.isArray(j?.jars)) jars.push(...j.jars);
  else if (Array.isArray(j?.data)) jars.push(...j.data);
}

jars = jars
  .filter(x => typeof x === 'string')
  .map(s => s.trim())
  .filter(Boolean);

const jarsOriginal = jars.slice();
const jarsSorted = jars.slice().sort();
const payload = jarsSorted.join('\n');

// Strategy version: bump this string whenever you change workflow logic to invalidate old cache
const strategyVersion = '5lines-v1';

// Default TTL: 7 days
const cacheTtlSeconds = 7 * 24 * 60 * 60;

const projectKey = crypto.createHash('sha1').update(payload).digest('hex');

return [{
  json: {
    jars: jarsSorted,
    jarsOriginal,
    jarsSorted,
    jarCount: jarsOriginal.length,
    projectKey,
    strategyVersion,
    cacheTtlSeconds
  }
}];

// Initialize scan config for 5-line audit workflow (Redis version).
const jars = Array.isArray($json.jars) ? $json.jars : [];
const jarCount = typeof $json.jarCount === 'number' ? $json.jarCount : jars.length;

// Prefer original jar ordering (if available) for gadgetDir heuristic
const jarsForGadget = Array.isArray($json.jarsOriginal) && $json.jarsOriginal.length
  ? $json.jarsOriginal
  : jars;

// Heuristic: derive a default gadgetDir from the first jar path
let gadgetDir = '';
if (jarsForGadget.length > 0) {
  const first = jarsForGadget[0];
  const idx = Math.max(first.lastIndexOf('/'), first.lastIndexOf('\\'));
  if (idx > 0) gadgetDir = first.slice(0, idx);
}

const scanConfig = {
  enableDfsTaint: false,

  // Line switches
  enableGraphLite: true,
  enableVulRules: true,
  enableScaLeak: true,
  scope: 'app',
  excludeNoise: true,
  limits: {
    auditFast: { callLimit: 14, mappingSampleLimit: 120, resourceSearchLimit: 80, strSearchPerKeyword: 20, maxCodeConfirm: 6 },
    graphLite: { callLimit: 16, sinkPerTypeLimit: 20, maxDepth: 4, maxChains: 60 },
    vulRules: { callLimit: 10, levels: ['high','medium'], groupBy: 'rule', perRuleLimit: 15, totalLimit: 160 },
    scaLeak: { callLimit: 10, enableSca: true, enableLeak: true, enableGadget: false, gadgetDir: gadgetDir, leakLimit: 120 },
    dfsTaint: { callLimit: 28, sinkNames: ['Runtime.exec(String)','ProcessBuilder.start','InitialContext.lookup','URL.openConnection'], depth: 10, maxLimit: 80, maxPaths: 80, timeoutMs: 90000, onlyFromWeb: true, resultLimit: 120, compact: true }
  }
};

return [{
  json: {
    ...$json,
    jars,
    jarCount,
    scanConfig
  }
}];

// Build Redis cacheKey for line: audit-fast
const strategyVersion = $json.strategyVersion || '5lines-v1';
const projectKey = $json.projectKey || 'unknown';
const cacheKey = `ja:5lines:${strategyVersion}:${projectKey}:audit-fast`;

return [{
  json: {
    ...$json,
    line: "audit-fast",
    cacheKey
  }
}];

// Interpret Redis GET output and decide cacheHit.
// Expected Redis node shape for GET: { value: <string|null> }.
// We only inspect known value fields to avoid accidentally treating other context strings as cache values.
const j = $json || {};

function getRedisValue(obj) {
  if (!obj || typeof obj !== 'object') return null;

  if (typeof obj.value === 'string' || obj.value === null) return obj.value;
  if (typeof obj.data === 'string' || obj.data === null) return obj.data;
  if (typeof obj.result === 'string' || obj.result === null) return obj.result;

  // Some variants may nest under "redis"
  if (obj.redis && typeof obj.redis === 'object') {
    const r = obj.redis;
    if (typeof r.value === 'string' || r.value === null) return r.value;
    if (typeof r.data === 'string' || r.data === null) return r.data;
    if (typeof r.result === 'string' || r.result === null) return r.result;
  }

  return null;
}

const rawVal = getRedisValue(j);
const rawStr = (typeof rawVal === 'string') ? rawVal : '';

let cacheHit = false;
let cacheParseOk = false;
let cachedRecord = null;
let cacheError = null;

if (rawStr && rawStr.trim().length > 0) {
  try {
    cachedRecord = JSON.parse(rawStr);
    cacheParseOk = true;
    cacheHit = true;
  } catch (e) {
    cacheError = `cache_json_parse_error: ${String(e)}`;
  }
}

return {
  json: {
    ...j,
    cacheHit,
    cacheParseOk,
    cacheError,
    cachedRecord,
    cacheValuePreview: rawStr.length > 500 ? rawStr.slice(0, 500) + '...(truncated)' : rawStr
  }
};

// Use cachedRecord for audit-fast
const j = $json || {};
const rec = j.cachedRecord;
let findings = [];
if (rec) {
  if (Array.isArray(rec.findings)) findings = rec.findings;
  else if (Array.isArray(rec)) findings = rec;
  else if (typeof rec === 'object') findings = [rec];
}
return [{
  json: {
    ...j,
    line: "audit-fast",
    cacheHit: true,
    parseOk: j.cacheParseOk === true,
    parseError: j.cacheError || "",
    findings
  }
}];

const ctx = $json || {};
const className = String(ctx.className || "unknown");
const entryTypes = Array.isArray(ctx.entryTypes) ? ctx.entryTypes : (ctx.entryType ? [ctx.entryType] : []);
const entryType = ctx.entryType || (entryTypes[0] || "");
const classInfo = ctx.classInfo || {};
const methods = Array.isArray(ctx.methods) ? ctx.methods : [];
const mappings = Array.isArray(ctx.mappings) ? ctx.mappings : [];

const lim = ($node["Init Scan Config"]?.json?.scanConfig?.limits?.auditFast) || {};
const methodListLimit = lim.methodListLimit || 120;
