// Parse AI Agent output into {line, findings[]} while preserving context fields (e.g., cacheKey/projectKey).
const ctx = $json || {};
const line = ctx.line || (ctx.context && ctx.context.line) || "unknown";

function extractText(v) {
  if (v === null || v === undefined) return "";
  if (typeof v === "string") return v;
  try { return JSON.stringify(v); } catch (e) { return String(v); }
}

let raw = ctx.output ?? ctx.text ?? ctx.data ?? ctx;
let s = extractText(raw).trim();

// Strip markdown fences if present
const fence = s.match(/```json\s*([\s\S]*?)```/i);
if (fence && fence[1]) s = fence[1].trim();

let findings = [];
let parseOk = false;
let parseError = "";

if (!s) {
  parseError = "empty_response";
} else {
  try {
    const obj = JSON.parse(s);
    if (Array.isArray(obj)) {
      findings = obj;
      parseOk = true;
    } else if (obj && typeof obj === "object") {
      if (Array.isArray(obj.findings)) {
        findings = obj.findings;
        parseOk = true;
      } else if (Array.isArray(obj.data)) {
        findings = obj.data;
        parseOk = true;
      } else {
        parseError = "json_object_without_findings_array";
      }
    } else {
      parseError = "json_not_array";
    }
  } catch (e) {
    parseError = e.message || String(e);
  }
}

return [{
  json: {
    ...ctx,
    line,
    parseOk,
    parseError,
    findings: Array.isArray(findings) ? findings : []
  }
}];

