function normalizeTrace(trace, fallback) {
  let t = [];
  if (Array.isArray(trace)) {
    t = trace.map(x => {
      const o = (x && typeof x === 'object') ? x : {};
      return {
        class: normalizeClassName(o.class || o.className || o.cls || ''),
        method: normStr(o.method || o.methodName || '').trim(),
        desc: normStr(o.desc || o.methodDesc || '').trim(),
      };
    }).filter(x => x.class || x.method || x.desc);
  }
  if (!t.length && fallback) {
    t = [fallback].filter(x => x.class || x.method || x.desc);
  }
  if (!t.length) t = [{class:'', method:'', desc:''}];
  return t;
}

function clampInt(n, min, max) {
  const x = parseInt(n, 10);
  if (Number.isNaN(x)) return null;
  return Math.min(max, Math.max(min, x));
}

function scoreBy(sev, conf) {
  const s = sev || 'low';
  const c = conf || 'low';
  const table = {
    'high':   {'high':9, 'medium':8, 'low':7},
    'medium': {'high':7, 'medium':6, 'low':5},
    'low':    {'high':4, 'medium':3, 'low':2},
  };
  return table[s]?.[c] ?? 3;
}

function maskSecret(v) {
  const s = normStr(v).trim();
  if (s.length <= 8) return s ? (s[0] + '***') : '';
  return s.slice(0,4) + '***' + s.slice(-4);
}

function canonicalType(line, type, f) {
  const t = normStr(type).trim();
  const ruleName = normStr(f.ruleName || f.rule || f.name || '').trim();
  const sinkName = normStr(f.sinkName || f.sink || '').trim();

  if (t.startsWith('vul-rule::')) return t;

  if (line === 'vul-rules') {
    if (ruleName) return `vul-rule::${ruleName}`;
    if (t) return `vul-rule::${t}`;
  }

  const sname = sinkName || t;
  if (/Runtime\.exec/i.test(sname) || /ProcessBuilder/i.test(sname)) return 'command_execution';
  if (/InitialContext\.lookup/i.test(sname) || /Context\.lookup/i.test(sname) || /DirContext\.search/i.test(sname) || /LdapContext\.search/i.test(sname)) return 'jndi_injection';
  if (/Statement\.execute|executeQuery|executeUpdate|prepareStatement/i.test(sname)) return 'sql_injection';
  if (/ObjectInputStream\.readObject|XMLDecoder\.readObject|Hessian.*readObject|XStream\.fromXML|Yaml\.load|JSON\.parseObject|ObjectMapper\.readValue/i.test(sname)) return 'insecure_deserialization';
  if (/URL\.openConnection|HttpURLConnection\.connect/i.test(sname)) return 'ssrf';
  if (/File(Input|Output)Stream\.new|RandomAccessFile\.new|File\.delete/i.test(sname)) return 'file_io';

  if (line === 'sca-leak') {
    if (t === 'secret_leak') return 'secret_leak';
    if (t === 'dependency_cve') return 'dependency_cve';
    if (t === 'gadget_presence' || t === 'deserialization_gadget') return t;
  }

