  return 'unknown';
}

function normalizeFinding(line, f, fromCache) {
  const obj = (f && typeof f === 'object') ? f : {};
  const out = {};

  out.line = normStr(obj.line || line || 'unknown').trim() || 'unknown';
  out.fromCache = !!fromCache;

  out.ruleName = normStr(obj.ruleName || obj.rule || obj.name || '').trim() || undefined;
  out.sinkName = normStr(obj.sinkName || obj.sink || '').trim() || undefined;
  out.type = canonicalType(out.line, obj.type, { ...obj, ruleName: out.ruleName, sinkName: out.sinkName });

  let sev = normLevel(obj.severity, 'low');
  let conf = normLevel(obj.confidence, 'low');

  if (out.type === 'secret_leak') { sev = 'high'; conf = 'high'; }

  const cvss = obj.cvss ?? (obj.evidence && obj.evidence.cvss);
  const cvssNum = typeof cvss === 'number' ? cvss : parseFloat(cvss);
  if (out.type === 'dependency_cve' && !Number.isNaN(cvssNum)) {
    if (cvssNum >= 9.0) sev = 'high';
    else if (cvssNum >= 7.0) sev = 'medium';
    else sev = 'low';
    conf = 'high';
  }

  out.severity = allowedSev.has(sev) ? sev : 'low';
  out.confidence = allowedConf.has(conf) ? conf : 'low';

  let score = clampInt(obj.score, 1, 10);
  if (score === null) {
    if (out.type === 'dependency_cve' && !Number.isNaN(cvssNum)) {
      score = Math.max(1, Math.min(10, Math.round(cvssNum)));
    } else {
      score = scoreBy(out.severity, out.confidence);
    }
  }
  out.score = score;

  const fallback = {
    class: normalizeClassName(obj.className || obj.class || ''),
    method: normStr(obj.methodName || obj.method || '').trim(),
    desc: normStr(obj.methodDesc || obj.desc || '').trim(),
  };
  out.trace = normalizeTrace(obj.trace, fallback);

  let reason = normStr(obj.reason || '').trim();
  if (!reason) {
