      const tn = normStr(obj.typeName || (obj.evidence && obj.evidence.typeName) || '').trim();
      const val = maskSecret(obj.value || (obj.evidence && obj.evidence.value));
      reason = `Potential secret leak (${tn}) value=${val}`;
    } else if (out.type === 'dependency_cve') {
      const cve = normStr(obj.cve || (obj.evidence && obj.evidence.cve) || '').trim();
