  if (reason.length > 500) reason = reason.slice(0, 500) + '...';
  out.reason = reason;

  if (obj.evidence && typeof obj.evidence === 'object') {
    out.evidence = obj.evidence;
  } else {
    const ev = {};
    for (const k of ['jarName','jarPath','actualPath','restfulType','path','value','typeName','cve','cvss','project','version','matchedJarNames','matchedJarPaths']) {
