mapper.select:
SELECT DISTINCT anno_name
FROM anno_table
WHERE class_name = #{className}

SELECT DISTINCT anno_name
FROM anno_table
WHERE class_name = #{className} AND method_name = #{methodName}

SELECT DISTINCT m.method_name AS method_name, m.class_name AS class_name, m.method_desc AS method_desc, jt.jar_name AS jar_name, jt.jid AS jar_id FROM anno_table a JOIN method_table m ON a.class_name = m.class_name AND a.method_name = m.method_name LEFT JOIN jar_table jt ON m.jar_id = jt.jid
a.anno_name IN
#{name}
