mapper.select:
SELECT DISTINCT m.method_name AS method_name, m.class_name AS class_name, m.method_desc AS method_desc, m.line_number AS line_number, jt.jar_name AS jar_name, jt.jid AS jar_id, a.anno_name AS anno_name, CASE WHEN a.method_name IS NULL OR a.method_name = '' THEN 'class' ELSE 'method' END AS anno_scope FROM anno_table a JOIN method_table m ON a.class_name = m.class_name AND a.jar_id = m.jar_id
AND a.method_name = m.method_name
AND (a.method_name IS NULL OR a.method_name = '')
AND (a.method_name = m.method_name OR a.method_name IS NULL OR a.method_name = '')
LEFT JOIN jar_table jt ON m.jar_id = jt.jid
a.anno_name IN
#{name}
a.anno_name LIKE CONCAT('%', #{name}, '%') OR
AND m.jar_id = #{jarId}
ORDER BY jt.jid ASC, m.class_name ASC, m.method_name ASC, m.method_desc ASC, a.anno_name ASC
LIMIT #{limit}
OFFSET #{offset}
