mapper.select:
SELECT m.method_name, m.class_name, m.method_desc, m.path, m.restful_type, m.jar_id, jt.jar_name FROM spring_method_table m LEFT JOIN jar_table jt ON m.jar_id = jt.jid
AND m.jar_id = #{jarId}
AND ( m.class_name LIKE CONCAT('%', #{keyword}, '%') OR m.method_name LIKE CONCAT('%', #{keyword}, '%') OR m.path LIKE CONCAT('%', #{keyword}, '%') OR m.restful_type LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY m.jar_id ASC, m.class_name ASC, m.path COLLATE NOCASE ASC, m.method_name ASC, m.method_desc ASC
LIMIT #{limit}
OFFSET #{offset}
