SELECT DISTINCT m.method_id, jt.jar_name, m.method_name, m.class_name, m.method_desc,
                m.is_static, m.access, m.jar_id
FROM method_table m
         INNER JOIN method_string_table ms ON ms.mid = m.method_id
         INNER JOIN string_value_table sv ON sv.sid = ms.sid
         LEFT JOIN jar_table jt ON m.jar_id = jt.jid

    sv.value LIKE #{pattern}

        AND m.jar_id = #{jarId}


        AND m.class_name LIKE CONCAT(#{classLike}, '%')



    LIMIT #{limit}

SELECT DISTINCT m.method_id, jt.jar_name, m.method_name, m.class_name, m.method_desc,
                m.is_static, m.access, m.jar_id
FROM method_table m
         INNER JOIN method_string_table ms ON ms.mid = m.method_id
         INNER JOIN string_value_table sv ON sv.sid = ms.sid
         LEFT JOIN jar_table jt ON m.jar_id = jt.jid

    sv.value = #{value}

        AND m.jar_id = #{jarId}


        AND m.class_name LIKE CONCAT(#{classLike}, '%')



    LIMIT #{limit}

SELECT DISTINCT m.method_id, jt.jar_name, m.method_name, m.class_name, m.method_desc, m.is_static, m.access, m.jar_id FROM string_fts f INNER JOIN string_value_table sv ON sv.sid = f.rowid INNER JOIN method_string_table ms ON ms.sid = sv.sid INNER JOIN method_table m ON ms.mid = m.method_id LEFT JOIN jar_table jt ON m.jar_id = jt.jid
f MATCH #{value}
AND m.jar_id = #{jarId}
AND m.class_name LIKE CONCAT(#{classLike}, '%')
LIMIT #{limit}
